from clientbot.handlers.horoscope.keyboards.inline import eastern_horoscope_animals_builder, inline_builder
from clientbot.handlers.horoscope.data.callback_datas import EasternAnimals
from loader import client_bot_router
from aiogram.types import Message
from aiogram.utils.i18n import gettext as _
